var editing;

function limpa_formulário_cep() {
    //Limpa valores do formulário de cep.
    if(editing==false) {
        document.getElementById('idstreet').value = ("");
        document.getElementById('iddistrict').value = ("");
    }
    //
    document.getElementById('idcity').value = ("");
    document.getElementById('iduf').value = ("");
    document.getElementById('idibge').value = ("");
    document.getElementById('idibgetela').value = ("");
}

function meu_callback(conteudo) {
    if (!("erro" in conteudo)) {
        //Atualiza os campos com os valores.
        if(editing==false) {
            document.getElementById('idstreet').value = (conteudo.logradouro);
            document.getElementById('iddistrict').value = (conteudo.bairro);
        }
        //
        document.getElementById('idcity').value = (conteudo.localidade);
        document.getElementById('iduf').value=(conteudo.uf);
        document.getElementById('idibge').value = (conteudo.ibge);
        document.getElementById('idibgetela').value = (conteudo.ibge);
    } //end if.
    else {
        //CEP não Encontrado.
        limpa_formulário_cep();
        alert("CEP não encontrado.");
    }
}

function pesquisacep(valor, edit=false) {

    editing = edit;

    //Nova variável "cep" somente com dígitos.
    var cep = valor.replace(/\D/g, '');

    //Verifica se campo cep possui valor informado.
    if (cep != "") {

        //Expressão regular para validar o CEP.
        var validacep = /^[0-9]{8}$/;

        //Valida o formato do CEP.
        if (validacep.test(cep)) {

            //Preenche os campos com "..." enquanto consulta webservice.
            if(editing==false) {
                document.getElementById('idstreet').value = "...";
                document.getElementById('iddistrict').value = "...";
            }
            //
            document.getElementById('idcity').value = "...";
            document.getElementById('iduf').value = "...";
            document.getElementById('idibge').value = "...";
            document.getElementById('idibgetela').value = "...";

            //Cria um elemento javascript.
            var script = document.createElement('script');

            //Sincroniza com o callback.
            script.src = 'https://viacep.com.br/ws/' + cep + '/json/?callback=meu_callback';

            //Insere script no documento e carrega o conteúdo.
            document.body.appendChild(script);

        } //end if.
        else {
            //cep é inválido.
            limpa_formulário_cep();
            alert("Formato de CEP inválido.");
        }
    } //end if.
    else {
        //cep sem valor, limpa formulário.
        limpa_formulário_cep();
    }
};
